const sliderContainer=document.querySelector(".slider-container"),sliderButtons=sliderContainer.querySelector(".slider-buttons"),sliderItems=sliderContainer.querySelectorAll(".slider-item"),sliderButtonClickHandler=e=>{e.preventDefault();const{target:t}=e;if("BUTTON"===t.tagName)if(t.classList.contains("active-button"))t.blur();else{const s=sliderButtons.querySelector(".active-button");s.classList.remove("active-button");const i=sliderContainer.querySelector(".slider-item-active");i.classList.remove("slider-item-active"),sliderItems.forEach(e=>{e.className.includes(t.id)&&(e.classList.add("slider-item-active"),t.classList.add("active-button"),t.blur())})}};sliderButtons.addEventListener("click",sliderButtonClickHandler);